from setuptools import setupimport osimport syscurrent_path = os.path.abspath(os.path.dirname(__file__))with open(os.path.join(current_path, 'README.rst'), encoding='utf-8') as file:      long_description = file.read()subpackages = []parent = 'nonsensepy'subpackages_path = os.path.join(current_path, 'nonsensepy')for subdir, _, _ in os.walk(subpackages_path):      subdirs = subdir.split(os.sep)      parent_index = subdirs.index(parent) + 1      subpackages.append('.'.join(subdirs[parent_index:]))setup(name='nonsensepy',      version='0.0.2',      description='Generates random strings',      long_description=long_description,      url='https://github.com/monzita/nonsensepy',      author='Monika Ilieva',      author_email='example@hidden.com',      license='MIT License',      keywords='nonsensepy generator string random',      packages=[*subpackages],      package_data={},      py_modules=['nonsensepy'],      install_requires = [],      classifiers=[      ],      zip_safe=True)